mapper.select:
SELECT * FROM mart_info
WHERE a_name LIKE CONCAT(#{ingredient}, ' %')
and a_price != '0';

mapper.insert:
INSERT INTO mart_info (
    P_SEQ, M_SEQ, M_NAME, A_SEQ, A_NAME, A_UNIT, A_PRICE,
    P_YEAR_MONTH, ADD_COL, P_DATE, M_TYPE_CODE, M_TYPE_NAME,
    M_GU_CODE, M_GU_NAME
) VALUES (
    #{P_SEQ}, #{M_SEQ}, #{M_NAME}, #{A_SEQ}, #{A_NAME}, #{A_UNIT}, #{A_PRICE},
    #{P_YEAR_MONTH}, #{ADD_COL}, #{P_DATE}, #{M_TYPE_CODE}, #{M_TYPE_NAME},
    #{M_GU_CODE}, #{M_GU_NAME}
)
